import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Axios from 'axios'

function Patientx() {

  const [email,setEmail]= useState("")
  const [password,setPassword]= useState("")
  const [name,setName]= useState("")
  const [patientid,setPatientid]= useState("")

  const addToList =() =>{
    Axios.post("http://localhost:3000/signup-patient",{email:email,password:password,name:name,patientid:patientid})
  }

  return (
    <>
      <h1 style={{ fontFamily: "sans-serif",textAlign:"center" }}>Patient SignUp!!</h1>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "80vh",
          width: "40vh 40vh",
          border: "1px solid gray", // Add a black border
          margin: "120px 540px 120px 540px", // Add margin around the component
        }}
      >
        <Form>
          <Form.Group className="mb-3" controlId="email">
            <Form.Label style={{ fontFamily: "sans-serif" }} htmlFor="email">Email</Form.Label>
            <Form.Control
              id="email"
              type="text"
              onChange={(e)=>{
                setEmail(e.target.value)
              }}
              style={{
                height: "50px",
                borderColor: "black",
                borderWidth: "1px",
              }}
              autoComplete="email"
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="password">
            <Form.Label style={{ fontFamily: "sans-serif" }} htmlFor="password">
              Password
            </Form.Label>
            <Form.Control
             id="password"
              type="password"
              onChange={(e)=>{
                setPassword(e.target.value)
              }}
              style={{
                height: "50px",
                borderColor: "black",
                borderWidth: "1px",
              }}
              autoComplete="password"
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="patientid">
            <Form.Label style={{ fontFamily: "sans-serif" }}>
              patientId
            </Form.Label>
            <Form.Control
              type="text"
              onChange={(e)=>{
                setPatientid(e.target.value)
              }}
              style={{
                height: "50px",
                borderColor: "black",
                borderWidth: "1px",
              }}
              autoComplete="patientid"
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="name">
            <Form.Label style={{ fontFamily: "sans-serif" }}>Name</Form.Label>
            <Form.Control
              type="text"
              onChange={(e)=>{
                setName(e.target.value)
              }}
              style={{
                height: "50px",
                borderColor: "black",
                borderWidth: "1px",
              }}
              autoComplete="name"
            />
          </Form.Group>

          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "20px", // Adjust the margin as needed
            }}
          >
            <Button
              variant="dark"
              type="submit"
              size="lg"
              style={{ fontFamily: "sans-serif" }}
              onClick={addToList}
            >
              SignUp
            </Button>
            <Button
              variant="dark"
              type="submit"
              size="lg"
              style={{ fontFamily: "sans-serif" }}
            >
              Login
            </Button>
          </div>
        </Form>
      </div>
    </>
  );
}

export default Patientx;
