import React from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

function Admin() {


  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const result = await axios.post("http://localhost:3001/auth/login", {
        username,
        password,
      });

      setCookies("access_token", result.data.token);
      window.localStorage.setItem("userID", result.data.userID);
      navigate("/");
    } catch (error) {
      console.error(error);
    }
  };
  return (
    <>
      <h1 style={{ fontFamily: "sans-serif",textAlign: "center" }}>Admin Login!!</h1>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "40vh",
          width: "50vh 50vh",
          border: "2px solid gray", // Add a black border
          margin: "120px 540px 120px 540px", // Add margin around the component
        }}
      >
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label size="lg" style={{ fontFamily: "sans-serif" }}>
              Email
            </Form.Label>
            <Form.Control
              type="email"
              size="lg"
              style={{
                height: "50px",
                borderRadius: "0",
                borderColor: "black",
                borderWidth: "2px",
              }}
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label size="lg" style={{ fontFamily: "sans-serif" }}>
              Password
            </Form.Label>
            <Form.Control
              type="password"
              size="lg"
              style={{
                height: "50px",
                borderRadius: "0",
                borderColor: "black",
                borderWidth: "2px",
              }}
            />
          </Form.Group>

          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "20px", // Adjust the margin as needed
            }}
          >
            <Button
              variant="dark"
              type="submit"
              size="lg"
              style={{ fontFamily: "sans-serif" }}
            >
              Login
            </Button>

            <Button
              variant="dark"
              type="submit"
              size="lg"
              style={{ fontFamily: "sans-serif" }}
            >
              SignUp
            </Button>
          </div>
        </Form>
      </div>
    </>
  );
}

export default Admin;
